import styled from "styled-components";

import Button from "../../components/button";
import Contact from "../../components/contactus";
import Subtitle from "../../components/subtitle";
import data from "../data";
import { useRouter } from "next/router";

const MainWapper = styled.div`
  position: relative;
  z-index: 30;
`;

const HomeWapper = styled.section`
  width: 100%;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: space-around;
  & > div {
    font-size: 2.5rem;
    padding: 4rem;
    width: 70%;
    height: 50vh;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    justify-content: space-around;
    & > h1 {
      font-size: 7rem;
      font-weight: 500;
    }
    & > p {
      font-family: ${(props) => props.theme.fonts.second};
    }
  }
  & > svg {
    height: 100%;
    width: 100%;
    padding: 4rem;
  }
`;

const ApprochWapper = styled.section`
  margin: 0 4rem;
`;
const ApprochgridWapper = styled.div`
  & > div:nth-child(even) {
    & > div {
      order: 2;
    }
  }
  & > div {
    display: flex;
    align-items: center;
    & > svg {
      width: 50%;
    }
    & > div {
      display: flex;
      flex-direction: column;
      align-items: flex-start;
      justify-content: space-around;
      font-size: 2.5rem;
      width: 60%;
      margin: 0 3rem;
      & > * {
        margin: 2rem 0;
      }
      & > h2 {
        font-weight: 500;
      }
      & > p {
        font-size: 2.5rem;
        font-family: ${(props) => props.theme.fonts.second};
      }
    }
  }
`;

const PriceingWapper = styled.section`
  margin: 0 4rem;
`;
const PriceinggridWapper = styled.div`
  display: flex;
  justify-content: space-around;
  font-size: 2.8rem;
  & > div {
    padding: 3rem;
    border-radius: 8px;
    box-shadow: 1px 2px 20px -10px black;
    margin: 2rem;
    & > h2 {
      text-align: center;
      font-weight: 500;
      font-size: 5rem;
    }
    & > ul {
      font-size: 2.5rem;
      list-style: none;
      font-family: ${(props) => props.theme.fonts.second};
      & > li {
        margin: 2rem 0;
      }
    }
    & > div {
      display: flex;
      align-items: center;
      font-family: ${(props) => props.theme.fonts.second};
      & > h2 {
        font-size: 5rem;
        padding: 0 3rem;
        border-right: 1px solid;
      }
      & > p {
        font-size: 1.7rem;
        margin: 0 1rem;
      }
    }
  }
`;
const Webdesign = () => {
  const router = useRouter();
  const { id } = router.query;
  console.log(id);
  if (id) {
    const rdata = data.filter((dt, i) => Number(id) === i);
    const {
      title,
      headcontent,
      Headimg,
      approchcontent,
      approchdata,
      priceingcontent,
      priceingdata,
    } = rdata;
    console.log(approchdata);
    return (
      <MainWapper>
        <HomeWapper id="home">
          <div>
            <h1>{title}</h1>
            <p>{headcontent}</p>
            <Button text="View plans" />
          </div>
          <Headimg />
        </HomeWapper>
        <ApprochWapper>
          <Subtitle title="Approch" info={approchcontent} />

          <ApprochgridWapper>
            {approchdata.map(({ name, Svg, content, goto }, i) => {
              return (
                <div key={i}>
                  <div>
                    <h2>{`${name}`}</h2>
                    <p>{content}</p>
                    <Button text="Contact us" goto={goto} />
                  </div>
                  <Svg />
                </div>
              );
            })}
          </ApprochgridWapper>
        </ApprochWapper>
        <PriceingWapper>
          <Subtitle title="Priceing" info={priceingcontent} />
          <PriceinggridWapper>
            {priceingdata.map(({ cost, extrfutr, ptitle, futrsoffer }, i) => {
              return (
                <div key={i}>
                  <h2>{ptitle}</h2>
                  <ul>
                    {futrsoffer.map((info, i) => {
                      return <li key={info}>{info}</li>;
                    })}
                  </ul>
                  <div>
                    <h2>${cost}</h2>
                    <p>{extrfutr}</p>
                  </div>
                </div>
              );
            })}
          </PriceinggridWapper>
        </PriceingWapper>
        <Contact />
      </MainWapper>
    );
  } else {
    return <div>Loading</div>;
  }
};
export default Webdesign;
